use std::env;

use dotenvy::dotenv;
use twilight_http::{request::channel::message::CreateMessage, Client};
use twilight_model::{
    id::{
        marker::ChannelMarker,
        Id,
    },
};
use twilight_model::id::marker::GuildMarker;

use crate::{clone_message, error::Error};

mod avatar;

// noinspection SpellCheckingInspection
#[rustfmt::skip]
const IMAGE_BASE64: &str = "data:image/png;base64,";

struct Context {
    http: Client,
    guild_id: Id<GuildMarker>,
    channel_id: Id<ChannelMarker>,
}

impl Context {
    async fn new() -> Self {
        dotenv().unwrap();

        let http = Client::new(env::var("BOT_TOKEN").unwrap());
        let channel_id = env::var("CHANNEL_ID").unwrap().parse().unwrap();
        let guild_id = http.channel(channel_id).await.unwrap().model().await.unwrap().guild_id.unwrap();

        Self {
            http,
            guild_id,
            channel_id,
        }
    }

    fn create_message(&self) -> CreateMessage<'_> {
        self.http
            .create_message(self.channel_id)
            .content("basic")
            .unwrap()
    }
}

#[tokio::test]
async fn basic() -> Result<(), Error> {
    let ctx = Context::new().await;

    let message = ctx.create_message().await?.model().await?;
    clone_message(&message, &ctx.http).await?;

    Ok(())
}
